"use strict";
const dateManip = require("date-manip");
const DEFAULT_FORMATTER = "HH:mm:ss.SSSZ";
function basicLogPrefix(event, dateFormat) {
  return `[${dateManip.format(event.timestamp, dateFormat || DEFAULT_FORMATTER)}] [${event.levelName}] [${event.loggerName}] -`;
}
module.exports = basicLogPrefix;
